import { ref, computed } from "vue";
import PostItem from "../components/PostItem.vue";
import postData from "../postData"; // 投稿データをインポート
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 投稿データを格納する変数
    const recsList = ref(postData); // postData を直接代入

    // 検索クエリに基づいて投稿をグループ化
    const groupedRecsList = computed(() => {
      if (!searchQuery.value) {
        // 検索クエリが空の場合、全ての投稿を表示
        return recsList.value.reduce((groups, post) => {
          const firstLetter = post.appName[0].toUpperCase(); // App名の頭文字
          if (!groups[firstLetter]) {
            groups[firstLetter] = [];
          }
          groups[firstLetter].push(post);
          return groups;
        }, {});
      }

      // 検索クエリに一致する投稿をグループ化
      return recsList.value.filter(post => post.appName.toLowerCase().startsWith(searchQuery.value.toLowerCase())).reduce((groups, post) => {
        const firstLetter = post.appName[0].toUpperCase(); // App名の頭文字
        if (!groups[firstLetter]) {
          groups[firstLetter] = [];
        }
        groups[firstLetter].push(post);
        return groups;
      }, {});
    });

    // エンターキーで検索を実行する関数
    const handleSearch = () => {
      console.log("検索実行:", searchQuery.value);
    };
    const __returned__ = {
      searchQuery,
      recsList,
      groupedRecsList,
      handleSearch,
      ref,
      computed,
      PostItem,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
