import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, computed } from "vue";
import { searchAndLogApps } from "../SteamWebAPI/SearchApps";
import PostItem from "../components/PostItem.vue";
import postData from "../postData";
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 投稿データを格納する変数
    const postList = ref(postData); // postData を直接代入

    // 検索結果を格納する変数
    const searchResults = ref([]);

    // 検索処理
    async function handleSearch() {
      if (!searchQuery.value) {
        searchResults.value = []; // 検索クエリが空の場合は検索結果をクリア
        return;
      }
      try {
        // searchAndLogAppsを呼び出して検索結果を取得
        const results = await searchAndLogApps(searchQuery.value);
        searchResults.value = results; // 検索結果を格納
      } catch (error) {
        console.error("検索中にエラーが発生しました:", error);
        searchResults.value = [];
      }
    }

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredPostList = computed(() => {
      if (!searchQuery.value) {
        return postList.value; // 検索クエリが空の場合はpostDataを表示
      }
      if (searchResults.value && searchResults.value.length > 0) {
        return searchResults.value; // 検索結果がある場合は検索結果を表示
      }
      return postList.value.filter(post => post.appName.toLowerCase().startsWith(searchQuery.value.toLowerCase())); // 検索クエリに基づいてpostDataをフィルタリング
    });
    const __returned__ = {
      searchQuery,
      postList,
      searchResults,
      handleSearch,
      filteredPostList,
      ref,
      computed,
      get searchAndLogApps() {
        return searchAndLogApps;
      },
      PostItem,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
