import { ref, computed } from "vue";
import SearchBar from "../components/SearchBar.vue";
import PostItem from "../components/PostItem.vue";
import postData from "../postData";

// 投稿データを格納する変数

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const postList = ref(postData); // postData を直接代入

    // 検索結果を格納する変数
    const searchResults = ref([]);

    // 検索処理
    async function handleSearch(query) {
      if (!query) {
        searchResults.value = []; // 検索クエリが空の場合は検索結果をクリア
        return;
      }
      try {
        const results = await searchAndLogApps(query);
        searchResults.value = results || []; // 検索結果が空の場合は空配列を設定
      } catch (error) {
        console.error("検索中にエラーが発生しました:", error);
        searchResults.value = []; // エラー時も空配列を設定
      }
    }

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredPostList = computed(() => {
      if (searchResults.value && searchResults.value.length > 0) {
        return searchResults.value; // 検索結果がある場合は検索結果を表示
      }
      return postList.value; // 検索結果がない場合はpostDataを表示
    });
    const __returned__ = {
      postList,
      searchResults,
      handleSearch,
      filteredPostList,
      ref,
      computed,
      SearchBar,
      PostItem,
      get postData() {
        return postData;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
