import { ref, computed } from "vue";
import postData from "../postData";
import PostItem from "../components/PostItem.vue";

// 投稿データを管理

export default {
  __name: 'TagsView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const posts = ref(postData);

    // 全タグを抽出しユニーク化
    const uniqueTags = computed(() => {
      const tags = posts.value.flatMap(post => post.tags || []);
      return [...new Set(tags)];
    });

    // 選択中のタグ（複数選択対応）
    const selectedTags = ref([]);

    // 選択したタグで投稿をフィルタ（いずれかのタグを含む投稿を表示）
    const filteredPosts = computed(() => {
      if (!selectedTags.value.length) return posts.value;
      return posts.value.filter(post => post.tags && post.tags.some(tag => selectedTags.value.includes(tag)));
    });
    const __returned__ = {
      posts,
      uniqueTags,
      selectedTags,
      filteredPosts,
      ref,
      computed,
      get postData() {
        return postData;
      },
      PostItem
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
