import postData from "../postData"; // 投稿データをインポート

// 投稿データを格納する変数

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const recsList = ref(null);

    // 投稿を取得
    recsList.value = postData; // postData を直接代入

    const __returned__ = {
      recsList,
      ref,
      get postData() {
        return postData;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
