import "core-js/modules/es.array.push.js";
import { ref, computed } from "vue";
import PlojectSearchBar from "../components/PlojectSearchBar.vue";
import PostItem from "../components/PostItem.vue";
import PostFunctionModalDialog from "../components/PostFunctionModalDialog.vue";
import postData from "../postData";
import plusIcon from "../assets/plus.svg";
export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const postList = ref(postData); // 
    const searchResults = ref([]); // 検索結果を管理
    const selectedPost = ref(null); // 選択された投稿データ
    const showModal = ref(false); // モーダルの表示状態を管理

    // 検索結果を更新する関数
    function updateSearchResults(results) {
      searchResults.value = results || [];
    }

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredPostList = computed(() => {
      if (searchResults.value && searchResults.value.length > 0) {
        return searchResults.value; // 検索結果がある場合は検索結果を表示
      }
      return postList.value; // 検索結果がない場合はpostDataを表示
    });

    // モーダルの表示/非表示を切り替える関数
    function toggleModal() {
      showModal.value = !showModal.value;
    }

    // 新しい投稿データを追加する関数
    function addPost(newPost) {
      postList.value.push(newPost); // 新しい投稿データを追加
    }
    const __returned__ = {
      postList,
      searchResults,
      selectedPost,
      showModal,
      updateSearchResults,
      filteredPostList,
      toggleModal,
      addPost,
      ref,
      computed,
      PlojectSearchBar,
      PostItem,
      PostFunctionModalDialog,
      get postData() {
        return postData;
      },
      get plusIcon() {
        return plusIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
