import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, computed, watch } from "vue";
import PostItem from "../components/PostItem.vue";
import postData from "../postData"; // 投稿データをインポート
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 投稿データを格納する変数
    const recsList = ref(postData); // postData を直接代入

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredRecsList = computed(() => {
      if (!searchQuery.value) {
        console.log("検索クエリが空です。すべての投稿を表示します。");
        return recsList.value;
      }
      console.log("検索クエリ:", searchQuery.value);
      const filtered = recsList.value.filter(post => post.appName.toLowerCase().startsWith(searchQuery.value.toLowerCase()));
      console.log("フィルタリング結果:", filtered);
      return filtered;
    });
    watch(searchQuery, newQuery => {
      console.log("検索クエリが更新されました:", newQuery);
    });
    const __returned__ = {
      searchQuery,
      recsList,
      filteredRecsList,
      ref,
      computed,
      watch,
      PostItem,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
