import { computed } from "vue";
import postData from "../postData";
import PostItem from "../components/PostItem.vue";

// ランキング上位10件を計算

export default {
  __name: 'RankingView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const top10Posts = computed(() => {
      return [...postData].sort((a, b) => b.likeCount - a.likeCount) // likeCountで降順ソート
      .slice(0, 10); // 上位10件を取得
    });
    const __returned__ = {
      top10Posts,
      computed,
      get postData() {
        return postData;
      },
      PostItem
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
