      },
      {
        text: "やり込み・チャレンジ重視",
        genrePoints: { 格闘: 1, アクション: 1, カードゲーム: 1 },
      },
      {
        text: "映画のような物語が好き",
        genrePoints: { アドベンチャー: 1, RPG: 1 },
      },
