      },
      {
        text: "世界に浸って冒険したい",
        genrePoints: { RPG: 1, アドベンチャー: 1 },
      },
    ],
  },
];

function handleSelect(selectedGenres) {
  console.log("selectedGenres:", selectedGenres); // デバッグログ

  if (!Array.isArray(selectedGenres)) {
    console.error("selectedGenres is not an array:", selectedGenres);
    return;
  }

  selectedGenres.forEach((genre) => {
    if (scores.value[genre] !== undefined) {
      scores.value[genre]++;
    }
  });

  if (currentIndex.value < questionData.length - 1) {
    currentIndex.value++;
  } else {
    const topGenre = Object.entries(scores.value).sort((a, b) => b[1] - a[1])[0][0];
    router.push({ name: "TestResultVIew", query: { genre: topGenre } });
  }
}

export default questionData;
