import { ref } from "vue";
import PostItem from "../components/PostItem.vue";
import { searchAndLogApps } from "../SteamWebAPI/SearchApps";
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 検索結果を格納する変数
    const filteredRecsList = ref([]);

    // 検索処理
    async function handleSearch() {
      if (!searchQuery.value) {
        filteredRecsList.value = [];
        return;
      }
      try {
        // searchAndLogAppsを呼び出して検索結果を取得
        const results = await searchAndLogApps(searchQuery.value);

        // 検索結果を更新
        filteredRecsList.value = results;
      } catch (error) {
        console.error("検索中にエラーが発生しました:", error);
        filteredRecsList.value = [];
      }
    }
    const __returned__ = {
      searchQuery,
      filteredRecsList,
      handleSearch,
      ref,
      PostItem,
      get searchAndLogApps() {
        return searchAndLogApps;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
