import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, computed } from "vue";
// import PostItem from "../components/PostItem.vue";
import postData from "../postData"; // 投稿データをインポート
import searchIcon from "../assets/search.svg";

//検索バーの入力値をリアクティブに管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref(""); // 検索クエリを管理
    // 
    const filteredRecsList = computed(() => {
      if (!searchQuery.value) {
        // 検索クエリが空の場合、すべての投稿を表示
        return recsList.value;
      }
      // 検索クエリに一致する投稿をフィルタリング
      return recsList.value.filter(post => post.appName.toLowerCase().includes(searchQuery.value.toLowerCase()));
    });

    // 投稿データを格納する変数
    const recsList = ref(null);

    // 投稿を取得
    recsList.value = postData; // postData を直接代入

    const __returned__ = {
      searchQuery,
      filteredRecsList,
      recsList,
      ref,
      computed,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
