import postData from "../postData"; // 投稿データをインポート
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // フィルタリングされたアプリのリストを計算
    const filteredApps = computed(() => {
      if (!searchQuery.value) {
        return recsList.value;
      }
      return recsList.value.filter(app => app.name.toLowerCase().includes(searchQuery.value.toLowerCase()));
    });
    // 投稿データを格納する変数
    const recsList = ref(null);

    // 投稿を取得
    recsList.value = postData; // postData を直接代入

    const __returned__ = {
      searchQuery,
      filteredApps,
      recsList,
      ref,
      computed,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
