import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, computed } from "vue";
import PostItem from "../components/PostItem.vue";
import postData from "../postData";
import { searchAndLogApps } from "../SteamWebAPI/SearchApps";
import searchIcon from "../assets/search.svg";

// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 投稿データを格納する変数
    const postList = ref(postData); // postData を直接代入

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredPostList = computed(() => {
      if (!searchQuery.value) {
        return postList.value;
      }
      return postList.value.filter(post => post.appName.toLowerCase().startsWith(searchQuery.value.toLowerCase()));
    });
    const __returned__ = {
      searchQuery,
      postList,
      filteredPostList,
      ref,
      computed,
      PostItem,
      get postData() {
        return postData;
      },
      get searchAndLogApps() {
        return searchAndLogApps;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
