// 検索クエリを管理

export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchQuery = ref("");

    // 投稿データを格納する変数
    const recsList = ref(postData); // postData を直接代入

    // 検索クエリに基づいてフィルタリングされた投稿データを計算
    const filteredRecsList = computed(() => {
      if (!searchQuery.value) {
        // 検索クエリが空の場合、すべての投稿を表示
        return recsList.value;
      }
      // 検索クエリに一致する投稿をフィルタリング（先頭一致）
      return recsList.value.filter(post => post.appName.toLowerCase().startsWith(searchQuery.value.toLowerCase()));
    });

    // エンターキーで検索を実行する関数
    const handleSearch = () => {
      console.log("検索実行:", searchQuery.value);
      // フィルタリングはリアルタイムで `filteredRecsList` に反映されるため、特別な処理は不要
    };
    const __returned__ = {
      searchQuery,
      recsList,
      filteredRecsList,
      handleSearch,
      ref,
      computed,
      PostItem,
      get postData() {
        return postData;
      },
      get searchIcon() {
        return searchIcon;
      }
    };
